import React, { Component } from 'react'
import '../App.css'
import axios from 'axios'
import cookie from 'react-cookies'
import { Redirect } from 'react-router'
import { Link } from 'react-router-dom'
import { loginuser } from '../actions'
import { Field, reduxForm } from 'redux-form'
import { connect } from 'react-redux'
// Define a Login Component
class Existingusersignin extends Component {
  // call the constructor method
  constructor (props) {
    // Call the constrictor of Super class i.e The Component
    super(props)
    // maintain the state required for this component
    this.state = {
      email: '',
      password: '',
      authFlag: false,
      authFailed: false
    }
    // Bind the handlers to this class
    // this.usernameChangeHandler = this.usernameChangeHandler.bind(this)
    // this.passwordChangeHandler = this.passwordChangeHandler.bind(this)
    // this.submitLogin = this.submitLogin.bind(this)
  }
  // Call the Will Mount to set the auth Flag to false
  componentWillMount () {
    this.setState({
      authFlag: false,
      authFailed: false
    })
  }
  renderError = ({ error, touched }) => {
    if (touched && error) {
      return (
        <div>
          <label style={{ color: 'red' }}>{error}</label>
        </div>
      )
    }
  }

  renderInput = ({ input, label, meta }) => {
    return (
      <div>
        <div htmlFor='email' style={{ color: '#6b6b83' }}>
          {label}
        </div>
        <div class='form-group'>
        <input class='form-control' {...input} />
        {this.renderError(meta)}
        </div>
      </div>
    )
  }

  onSubmit = formValues => {
    console.log('OnSubmit' + formValues)
    let data = {
      email: formValues.email,
      password: formValues.password
    }
    axios.defaults.withCredentials = true
    this.props.loginuser(data, res => {
      if (res.status === 200) {
        console.log('Inside response',res.data[0]);
        this.setState({
          authFlag:true
        })
        sessionStorage.setItem('email', res.data[0].email)
        sessionStorage.setItem('username', res.data[0].username)
        sessionStorage.setItem('cookie', res.data[0].type)
        // cookies.save('cookie', res.data[0].type, {maxAge: 900000, httpOnly: false, path: '/'});
        // cookies.save('userid', res.data[0]._id, {maxAge: 900000, httpOnly: false, path: '/'});
        // cookies.save('username', res.data[0].username, {maxAge: 900000, httpOnly: false, path: '/'});
        this.props.history.push("/search");
      } else {
        alert('Please enter valid credentials')
      }
    })

  }

  inputChangeHandler = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  // submit Login handler to send a request to the node backend
  submitLogin = e => {
    var headers = new Headers()
    console.log('submit login')
    // prevent page from refresh
    e.preventDefault()
    const data = {
      email: this.state.email,
      password: this.state.password
    }
    // set the with credentials to true
    axios.defaults.withCredentials = true
    console.log(data)
    // make a post request with the user data
    axios
      .post('http://localhost:3001/login', data)
      .then(response => {
        console.log('Status Code : ', response.status)
        if (response.status === 200) {
          sessionStorage.setItem('email', data.email)
          this.setState({
            authFlag: true
          })
        }
      })
      .catch(err => {
        this.setState({ authFailed: true })
      })
  }

  render () {
    console.log(this.props.user)
    // redirect based on successful login
    // console.log(this.props)

    // renderInput(formProps){
    //   return (<input onChange={...formProps.input.onChange} value={...formProps.input.value} />)
    // }

    let redirectVar = null
    let invalidtag = null
    if (!cookie.load('cookie')) {
      redirectVar = <Redirect to='/login' />
    }
    let redirecthome = null
    if (this.state.authFlag) {
      redirecthome = <Redirect to='/home' />
    }
    if (this.state.authFailed) {
      invalidtag = (
        <label style={{ color: 'red' }}>*Invalid user name password!</label>
      )
    }

    return (
      <form
        className='ui form error'
        onSubmit={this.props.handleSubmit(this.onSubmit)}
      >
        <div>
          {redirectVar}
          {redirecthome}
          <div class='container'>
            <div class='login-form'>
              <div class='main-div'>
                <div class='panel'>
                  <h2>Sign in with your Grubhub account</h2>
                  {invalidtag}
                </div>
                <Field
                  name='email'
                  component={this.renderInput}
                  label='Email'
                />
                <br />
                <Field
                  name='password'
                  type='password'
                  component={this.renderInput}
                  label='Password'
                />
                <br />
                <button type='submit' class='btn btn-primary'>
                  Login
                </button>
                <br />

                <div class='form-group'>
                  <div style={{ textAlign: 'center' }}>or</div>
                </div>
                <div class='form-group'>
                  <button class='btn btn-secondary'>
                    Continue with Facebook
                  </button>
                </div>

                <div class='form-group'>
                  <button class='btn btn-info'>Continue with Google</button>
                </div>

                <div style={{ textAlign: 'center' }} class='form-group'>
                  <Link to='/signup'>Create an account</Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    )
  }
}

const validate = formValues => {
  const error = {}
  if (!formValues.email) {
    error.email = 'Enter a valid email'
  }
  if (!formValues.password) {
    error.password = 'Enter a valid Password'
  }
  return error
}


// export default connect(null,{loginuser:loginuser})(formWrapped)
const mapStateToProps = state => {
  return { user: state.user }
}

export default connect(
  mapStateToProps,
  { loginuser }
)(
  reduxForm({
    form: 'streamLogin',
    validate: validate
  })(Existingusersignin)
)
