import React, {Component} from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import {Link} from 'react-router-dom';

//Define a Login Component
class Existingusersignin extends Component{
    //call the constructor method
    constructor(props){
        //Call the constructor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            Email : "",
            password : "",
            authFlag : false,
            failuser: false,
            failcatch:false
        }
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount(){
        this.setState({
            authFlag : false
        })
    }
    //username change handler to update state variable with the text entered by the user

    inputChangeHandler = (e) => {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    //submit Login handler to send a request to the node backend
    onSubmit = (values) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            Email : this.state.Email,
            password : this.state.password
        }
        //set the with credentials to true
        this.props.submitlogin(data);
    }

    render(){
        //redirect based on successful login
        let redirectVar1 = null;
        if(!cookie.load('cookie')){
            redirectVar1 = <Redirect to= "/login"/>
        }
        let redirectVar = null;
        if(this.state.authFlag){
            redirectVar = <Redirect to= "/lets-eat"/>
        }
        let faillabelcatch = null;
        if(this.state.failcatch){
            faillabelcatch=(
            <label style = {{color:"red"}}>Please check username and password</label>
            )
        }
        const {handleSubmit} = this.props;
        return(
            <div>
                {redirectVar1}
                {redirectVar}

            <div class="container">
                <form onSubmit={handleSubmit(this.onSubmit.bind(this))}>
                <div class="login-form">
                    <div class="main-div">
                        <div class="panel">
                            <h2 style={{fontWeight:"bold",color:"black"}}>Sign in with your Grubhub account</h2>


                        </div>  
                            <div class="form-group">
                                <div>Email</div>
                                <input onChange = {this.inputChangeHandler} type="text" class="form-control" autoFocus name="Email" />
                            </div>
                            <div class="form-group">
                                <div>Password</div>
                                <input onChange = {this.inputChangeHandler} type="password" class="form-control" name="password" />
                            </div><br></br>

                            <button type = "submit" onClick = {this.submitLogin} className ="button">Sign in</button> <br></br><br></br>
                            {faillabelcatch}
                            <div style = {{textAlign:"center"}}>or</div>

                            <div className="heading1">

                            </div><br></br>
                            <div >
                            <button className="fbbutton"> Continue with Facebook </button>  
                            </div><br></br>  
                            <div >
                            <button className="googlebutton"> Continue with Google </button>  
                            </div> <br></br>
                            <div style={{textAlign:"center"}}><Link to="/signup">Create your account</Link></div>
                            </div> 
                    </div>
                    </form>
                </div>

            </div>
        )
    }
}

const mapStateToProps = state => ({
    user: state.user
});

function validate(values) {
    const errors = {};
    if (!values.Email) {
        errors.Email = "Enter E-mail";
    }
    if (!values.password) {
        errors.password = "Enter Password";
    }
    return errors;
}

//export default Login;
export default reduxForm({
    validate,
    form: "loginForm"
})(connect(mapStateToProps, { submitlogin })(Existingusersignin));
