import React, { Component } from 'react';
import '../App.css';
import '../css/Buyerprofile.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import '../css/new.css';
import { getProfile } from '../actions'
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import NavBar from './NavBar.js';

//Define a Login Component
class Buyerprofile extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            firstname: "",
            lastname: "",
            profilepic: '',
            Email: "",
            password: "",
            nameflag: false,
            authFlag: false,
            failuser: false,
            selectedFile: null,
            phone: '',
            newEmail: ''
        }
    }

    //Call the Will Mount to set the auth Flag to false

    componentWillMount () {
        let path = process.env.PUBLIC_URL
        path = path + '/profilepics/Avatar.png'

        this.setState({
          authFlag: false,
          authFailed: false,
          nameflag: false,
          contactflag: false,
          img: path,
          passwordflag: false
        })

        let temp = sessionStorage.getItem('email')

        let data = { email: temp }
        console.log('Inside will mount: data value is: ' + data.email)

        this.props.getProfile({ params: data }, (data)=>{
          console.log('Redu test: ' + this.props.user)
          this.setState({
            email: data.email,
            phonenumber: data.phone,
            password: data.password,
            firstname: data.first_name,
            lastname: data.last_name
          })
        });


    }
    imageChangeHandler = e => {
        // console.log('image change handle name: ' + e.target.name)
        // console.log('image change handle value: ' + e.target.files[0])
        this.setState({
          file: e.target.files[0]
        })
      }

      uploadImage = e => {
        e.preventDefault()
        // var headers = new Headers();
        const formData = new FormData()
        // console.log(this.state.file.name)

        let email = sessionStorage.getItem('email')
        // this.setState({file.name:email})
        // var imagedata = document.querySelector('input[type="file"]').files[0];
        formData.append('myImage', this.state.file, email)
        // formData.append('myImage', this.state.file, email )
        // formData.append('email', email)
        const config = {
          headers: {
            'content-type': 'multipart/form-data'
          }
        }
        axios
          .post('/userprofile', formData, config)
          .then(response => {
            let data={'email':email}
            axios.post('http://localhost:3001/userimage', data).then(response => {
              // update the state with the response data
              console.log('Axios get:', response.data)
              this.setState({
                img: 'data:image/png;base64, ' + response.data
              })
            })
            console.log('The file is successfully uploaded')
          })
          .catch(error => {})
        // prevent page from refresh
      }

      onSubmitUsername = (formValues)=> {
        console.log('OnSubmit'+formValues)
        let data= {
          firstname: formValues.firstname,
          lastname: formValues.lastname,
          email: formValues.email,
          password: formValues.password
        }
        axios.defaults.withCredentials = true
