import React, {Component} from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import {Link} from 'react-router-dom';

class Letseat extends Component {
    constructor(){
        super();
        this.state = {  

        }
    }  
    //get the books data from backend  
    componentDidMount(){
        axios.get('http://localhost:3001/home')
                .then((response) => {
                //update the state with the response data
                this.setState({
                    books : this.state.books.concat(response.data) 
                });
            });
    }

    render(){
        //iterate over books to create a table row
        let details = this.state.books.map(book => {
            return(
                <tr>
                    <td>{book.BookID}</td>
                    <td>{book.Title}</td>
                    <td>{book.Author}</td>
                </tr>
            )
        })
        //if not logged in go to login page
        let redirectVar = null;
        if(!cookie.load('cookie')){
            redirectVar = <Redirect to= "/login"/>
        }
        return(
            <div>
                {redirectVar}
            <nav class="navbar navbar-inverse">
            <div style = {{color:"red"}} >GRUBHUB</div>
                {/* <div class="container-fluid"> */}
                    {/* <div class="navbar-header"> */}
                    {/* <img  src={require('../images/grubhub.svg')}></img> */}
                    {/* </div> */}
                    <ul class="nav navbar-nav">
                        {/* <li class="active"><Link to="/home">Home</Link></li> */}
                        <li><Link to="/Existingusersignin">Sign in</Link></li>
                        {/* <li><Link to="/Ownersignup">Owner Signup</Link></li> */}
                    </ul>

                    {/* {navLogin} */}
                {/* </div> */}
            </nav>
        </div>
        )
    }
}
//export Home Component
export default Letseat;
