import React, { Component } from 'react'
import axios from 'axios'
import '../App.css';
import '../css/Buyerprofile.css';
import cookie from 'react-cookies'
import { Redirect } from 'react-router'
import { Link } from 'react-router-dom'
import {getOwnerProfile} from '../actions'
import {getUserImage} from '../actions'
import {menudetails} from '../actions'
import { connect } from 'react-redux'

import {Field, reduxForm} from 'redux-form';
import '../css/new.css';

//Define a Login Component
class Ownerprofile extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            first_name: "",
            last_name: "",
            pic: '',
            email: "",
            restuarant_zipcode: "",
            restaurant_name: "",
            password: "",
            nameflag: false,
            authFlag: false,
            failuser: false,
            phone: '',
            newEmail: ''
        }
    }

    componentWillMount () {
        let path = process.env.PUBLIC_URL
        path = path + '/profile/Avatar.png'
        this.setState({
          authFlag: false,
          authFailed: false,
          nameflag: false,
          contactflag: false,
          restaurantflag: false,
          pic: path,
          passwordflag: false,

        })

        let temp = sessionStorage.getItem('email')

        let data = { email: temp }
        // console.log('Inside will mount: data value is: ' + data.email)


        this.props.getOwnerProfile({ params: data }, response => {
          // console.log( this.props.user[1])
          console.log(response.data)
          // console.log('Axios get:', response.data)
            this.setState({
              email: response.data.email,
              phone: response.data.phone,
              restaurant_name: response.data.restaurant_name,
              restaurant_zipcode: response.data.restaurant_zipcode,
              password: response.data.password,
              first_name: response.data.first_name,
              last_name: response.data.last_name
        })
      })

    }

    uploadImage = e => {
        e.preventDefault()
        // var headers = new Headers();
        const formData = new FormData()
        // console.log(this.state.file.name)

        let email = sessionStorage.getItem('email')
        // this.setState({file.name:email})
        // var imagedata = document.querySelector('input[type="file"]').files[0];
        formData.append('myImage', this.state.file, email)
        // formData.append('myImage', this.state.file, email )
        // formData.append('email', email)
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        axios
            .post('/ownerprofile', formData, config)
            .then(response => {
                let data = { 'email': email }
                axios.post('http://localhost:3001/ownerimage', data).then(response => {
                    // update the state with the response data
                    console.log('Axios get:', response.data)
                    this.setState({
                        pic: 'data:image/png;base64, ' + response.data
                    })
                })

            })
            .catch(error => { })
    }



    //username change handler to update state variable with the text entered by the user
    inputChangeHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    imageChangeHandler = e => {
        // console.log('image change handle name: ' + e.target.name)
        // console.log('image change handle value: ' + e.target.files[0])
        this.setState({
            file: e.target.files[0]
        })
    }


    update = (e) => {
        const data = {
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            email: this.state.email,
            phone: this.state.phone,
            password: this.state.password,
            restaurant_name: this.state.restaurant_name,
            restaurant_zipcode: this.state.restuarant_zipcode
        }
        console.log(data);
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001//ownerupdate', data)
            .then(response => {
                console.log("Status Code  is : ", response.status);
                console.log(response.data[0]);
                if (response.status === 200) {
                    this.setState({
                        first_name: response.data[0].first_name,
                        last_name: response.data[0].last_name,
                        password: response.data[0].password,
                        email: response.data[0].email,
                        phone: response.data[0].phone, 
                        restaurant_name: this.state.restaurant_name,
                        restaurant_zipcode: this.state.restaurant_zipcode

                    });
                    alert("Profile Update Succefully");
                } else {
                    console.log('Change failed !!! ');

                }
            });
    }



    render() {
        let updatePic = null

        if (this.state.file !== '') {
            updatePic = (
                <button
                    type='submit'
                >
                    Update
        </button>
            )
        }
        //redirect based on successful login
