import React, { Component } from 'react';
import '../App.css';
import '../css/bootstrap.css';
import axios from 'axios';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import Navbar from './NavBar';
import Letseat from './Letseat';

class Searchitem extends Component {
       constructor(props){
           super(props);
           this.state = {
             ip :[],
              flag : '',
              p : ''
           }
      }

      search=(rest)=>{
          this.setState({
            p :rest,
            flag : true,

          })
          var restaurants = this.props.location.state.result;
          var restaurant1 = restaurants.map((restaurant)=>{
            console.log(restaurant);

          })

        }

        componentDidMount(){
          let imagePreview = '';
          var restaurants = this.props.location.state.result;
          console.log(restaurants);
          var restaurant1 = restaurants.map((restaurant)=>{
            console.log(restaurant.restaurant_name);
            sessionStorage.setItem('restaurantname',restaurant.restaurant_name);


            axios.post('http://localhost:3001/getrestaurantpicsingle/'+`${restaurant.restaurant_name}`)
            .then(response => {
                console.log("Imgae Res : ",response);
                imagePreview = 'data:image/jpg;base64, ' + response.data;
                this.setState(  
                  { ip : this.state.ip.concat(imagePreview)}              
                )
               console.log(this.state.ip)

            });

          });
        }



    render(){  
      let redirectVar1 = null;
        if(!cookie.load('cookie')){
            redirectVar1 = <Redirect to= "/login"/>
        }

      if (this.state.flag){
        return (<Redirect to={{
          pathname: '/userorder',
          state: { result: this.state.p }
      }} />)
      }

      var restaurants = this.props.location.state.result;
      let i=-1;
      var restaurant1 = restaurants.map((restaurant)=>{
        i = i+1;

        return (
          <li className="list-group-item" onClick= {(e)=>this.search(restaurant)} >
          <div className="ima">
          <div className="media-left">

          <img className="media-object" src = {this.state.ip[i] } style={{width : "200px", height:"200px"}} />
          </div> 
          <div className="media-body">
          <div className="media-heading">
          <div col-md-8>
          </div>
           <div >

          </div>

          <div>
           {restaurant.restaurant_name}
          </div>
          <br></br>


          </div>
          </div>
          </div>
          </li>
        );

      }) 


    return(

      <div className ="container-fluid">
        <Letseat />
      {redirectVar1}
      <div className="container-fluid" style={{ backgroundColor : "#f4f4f4",height : "650px"}}>
        <div style={{margin : "3%", padding: "15px"}}> 
      <ul className="col-md-8 list-group" >
         <div style={{color:"black",fontWeight:"bold"}}>{restaurant1}
         </div>
     </ul> 
     </div>
     </div>
     </div>


    )

  }
}


export default Searchitem;
