import React, { Component } from 'react';
import '../App.css';
import '../css/Buyerprofile.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import '../css/new.css';

//Define a Login Component
class Buyerprofile extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            firstname: "",
            lastname: "",
            profilepic: '',
            Email: "",
            password: "",
            nameflag: false,
            authFlag: false,
            failuser: false,
            selectedFile: null,

        }

    }
    //Call the Will Mount to set the auth Flag to false

    // componentDidMount(){
    //     console.log("cookie" + cookie.load('cookie'))
    //     let Email=cookie.load('cookie')
    //     axios.get('https://localhost:30001/userprofile',{
    //         params : Email
    //     }).then(response=>{
    //         console.log(response.data)
    //         this.setState({
    //         firstname: response.data.firstname,
    //         lastname: response.data.lastname,
    //         profilepic: response.data.profilepic,
    //         password: response.data.password
    //         })
    //     })
    // }

    // componentWillMount(){
    //     axios.post('http://localhost:3001/getprofilepic'+`profile_${cookie.load('cookie')}.png`)
    //         .then(response=>{
    //             console.log(response);
    //             let newpic = 'data:image/jpg;base64, ' + response.data;
    //           this.setState({
    //               profilepic:newpic
    //           })
    //         })
    //     }

    nameflagHandler = e => {
        this.setState({
            nameflag: true
        })
    }
    fileSelectedHandler = e => {
        console.log(e.target.files);
        if (e.target.name == 'selectedFile') {
            this.setState({
                selectedFile: e.target.files[0]
            })
        } else {
            this.setState({ [e.target.name]: e.target.value });
        }
    }

    uploadHandler = e => {
        const fd = new FormData();
        fd.append('selectedFile', this.state.selectedFile, this.state.selectedFile.name);
