import React, { Component } from 'react'
import '../App.css'
import axios from 'axios'
import cookie from 'react-cookies'
import { Redirect } from 'react-router'
import { Link } from 'react-router-dom'
import { loginuser } from '../actions'
import { Field, reduxForm } from 'redux-form'
import { connect } from 'react-redux'
// Define a Login Component
class Existingusersignin extends Component {
  // call the constructor method
  constructor(props) {
    // Call the constrictor of Super class i.e The Component
    super(props)
    // maintain the state required for this component
    this.state = {
      email: '',
      password: '',
      authFlag: false,
      authFailed: false
    }

  }

  componentWillMount() {
    this.setState({
      authFlag: false,
      authFailed: false
    })
  }
  renderError = ({ error, touched }) => {
    if (touched && error) {
      return (
        <div>
          <label style={{ color: 'red' }}>{error}</label>
        </div>
      )
    }
  }

  renderInput = ({ input, label, meta }) => {
    return (
      <div>
        <div htmlFor='email' style={{ color: '#6b6b83' }}>
          {label}
        </div>
        <div class='form-group'>
          <input class='form-control' {...input} />
          {this.renderError(meta)}
        </div>
      </div>
    )
  }

  onSubmit(values) {
    this.props.loginuser(values);
  }

  render() {
    var errBlock = "";
    var red;
    var redirectVar;
    console.log(this.props.user.status)
    if (sessionStorage.getItem("JWT") != null) {
      redirectVar = <Redirect to="/home" />
    }

    this.props.user.status == 201 ? errBlock = <div className="login-err">
      <h4 style={{ color: "white", textAlign: "center" }}>Username or password incorrect</h4>
    </div> : null;

    // this.props.user.status == 202 ? errBlock = <div className="login-err">
    //   <h4 style={{ color: "white", textAlign: "center" }}>No user found, sign up</h4>
    // </div> : null;

    red = this.props.user.status == 200 && sessionStorage.getItem("JWT") != null || undefined ? <Redirect to='/home' /> : null
    const { handleSubmit } = this.props;

    return (


      <form

        className='ui form error'
        onSubmit={this.props.handleSubmit(this.onSubmit)}
      >
        <div>
          {redirectVar}
          {red}
          <div class='container'>
            <div class='login-form'>
              <div class='main-div'>
                <div class='panel'>
                  <h2>Sign in with your Grubhub account</h2>
                </div>
                <Field
                  name='email'
                  component={this.renderInput}
                  label='Email'
                />
                <br />
                <Field
                  name='password'
                  type='password'
                  component={this.renderInput}
                  label='Password'
                />
                <br />
                <button style={{ backgroundColor: "red" }} type='submit' class='btn btn-primary'>
                  Login
                    </button>
                <br />

                <div class='form-group'>
                  <div style={{ textAlign: 'center' }}>or</div>
                </div>


                <button type='submit' class='btn btn-primary'>Continue with Facebook</button>
                <br></br><br />

                <button class="btn btn-primary">Continue with Google</button>
                <br></br>
                <br></br>


                <div style={{ textAlign: 'center' }} class='form-group'>
                  <Link to='/signup'>Create an account</Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
    )
  }
}

