import React, {Component} from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import {Link} from 'react-router-dom';

//Define a Login Component
class Existingusersignin extends Component{
    //call the constructor method
    constructor(props){
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            Location : "",
            Foodtype : "",
            authFlag : false,
            failuser: false
        }
        //Bind the handlers to this class
        this.locationChangeHandler = this.locationChangeHandler.bind(this);
        this.foodtypeChangeHandler = this.foodtypeChangeHandler.bind(this);
        this.findFood = this.findFood.bind(this);
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount(){
        this.setState({
            authFlag : false
        })
    }
    //username change handler to update state variable with the text entered by the user

    locationChangeHandler= (e) => {
        this.setState({
            Location : e.target.value
        })
    }
    //password change handler to update state variable with the text entered by the user
    foodtypeChangeHandler = (e) => {
        this.setState({
            Foodtype : e.target.value
        })
    }
    //submit Login handler to send a request to the node backend
    findFood = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            Location : this.state.Location,
            Foodtype : this.state.Foodtype
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/Letseat',data)
            .then(response => {
                console.log("Status Code : ",response.status);
                if(response.status === 200){
                    // <Redirect to="/home"> </Redirect>
                    this.setState({
                        authFlag : true
                    })
                }
                else{
                    this.setState({
                    authFlag : false

                    })
                }
            })
    }

    render(){
        //redirect based on successful login
        let redirectVar = null;
        if(cookie.load('cookie')){
            redirectVar = <Redirect to= "/Letseat"/>
        }

        return(
            <div>
                {/* {redirectVar} */}
            <div class="container">

                <div class="login-form">
                    {/* <div class="main-div"> */}
                        <div class="panel">
                            <h2>Who delivers in your neighbourhood?</h2>

                        </div>  
                            <div class="form-group">
                                <input onChange = {this.locationChangeHandler} type="text" class="form-control" autoFocus name="Location" placeholder="Street address or zipcode"/>
                            </div>
                            <div class="form-group">
                                <input onChange = {this.foodtypeChangeHandler} type="text" class="form-control" name="FoodType" placeholder="pizza, sushi, c..."/>
                            </div><br></br>


                            <button type = "submit" onClick = {this.submitLogin} className ="button">Sign in</button> <br></br>

                            <div style = {{textAlign:"center"}}>or</div>

                            <div className="heading1">

                            </div><br></br>
                            <div >
                            <button className="fbbutton"> Continue with Facebook </button>  
                            </div><br></br>  
                            <div >
                            <button className="googlebutton"> Continue with Google </button>  
                            </div> <br></br>
                            <div><Link to="/Buyersignup">Create your account</Link></div>
                            </div> 
                    </div>
                </div>
            // </div>
        )
    }
}
//export Login Component
export default Existingusersignin;
