import React, {Component} from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';

//Define a Login Component
class Ownersignup extends Component{
    //call the constructor method
    constructor(props){
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            Name : "",
            Email : "",
            password : "",
            RestaurantName : "",
            RestaurantZipcode : "",
            authFlag : false,
            failuser: false
        }
        //Bind the handlers to this class
        this.nameChangeHandler = this.nameChangeHandler.bind(this);
        this.emailChangeHandler = this.emailChangeHandler.bind(this);
        this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
        this.restaurantNameHandler = this.restaurantNameChangeHandler.bind(this);
        this.restaurantZipcodeHandler = this.restaurantZipcodeChangeHandler.bind(this);
        this.submitOwnerSignup = this.submitOwnerSignup.bind(this);
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount(){
        this.setState({
            authFlag : false
        })
    }
    //username change handler to update state variable with the text entered by the user
    nameChangeHandler = (e) => {
        this.setState({
            Name : e.target.value
        })
    }



    emailChangeHandler = (e) => {
        this.setState({
            Email : e.target.value
        })
    }
    //password change handler to update state variable with the text entered by the user
    passwordChangeHandler = (e) => {
        this.setState({
            password : e.target.value
        })
    }

    restaurantNameChangeHandler = (e) => {
        this.setState({
            Name : e.target.value
        })
    }

    restaurantZipcodeChangeHandler = (e) => {
        this.setState({
            Name : e.target.value
        })
    }
    //submit Login handler to send a request to the node backend
    submitOwnerSignup = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            Name : this.state.Name,
            Email : this.state.Email,
            password : this.state.password,
            RestaurantName :this.state.RestaurantName,
            RestaurantZipcode : this.state.RestaurantZipcode
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/Ownersignup',data)
            .then(response => {
                console.log("Status Code : ",response.status);
                if(response.status === 200){
                    // <Redirect to="/home"> </Redirect>
                    this.setState({
                        authFlag : true
                    })
                }
                else{
                    this.setState({
                    authFlag : false

                    })
                }
            })
    }

    render(){
        //redirect based on successful login
        let redirectVar = null;
        if(cookie.load('cookie')){
            redirectVar = <Redirect to= "/"/>
        }

        return(
            <div>
            <div class="container">

                <div class="login-form">
                    <div class="main-div">
                        <div class="panel">
                            <h2>Sign in with your Grubhub</h2>
                            <h2>account</h2>
                        </div>  
                            <div class="form-group">
                                <input onChange = {this.NameChangeHandler} type="text" class="form-control" name="Name" placeholder="Name"/>
                            </div>
                            <div class="form-group">
                                <input onChange = {this.emailChangeHandler} type="text" class="form-control" name="Email" placeholder="Email"/>
                            </div>
                            <div class="form-group">
                                <input onChange = {this.passwordChangeHandler} type="password" class="form-control" name="password" placeholder="Password"/>
                            </div><br></br>
                            <div class="form-group">
                                <input onChange = {this.restaurantNameChangeHandler} type="text" class="form-control" name="RestaurantName" placeholder="Restaurant Name"/>
                            </div>
                            <div class="form-group">
                                <input onChange = {this.restaurantZipcodeHandler} type="text" class="form-control" name="RestaurantZipcode" placeholder="Zip code"/>
                            </div>
                            <button onClick = {this.submitLogin} class="btn btn-primary">Create your account</button> <br></br>

                            <div className="heading1">

                            </div><br></br>
                            <div >
                            <button className="fbbutton"> Log in with Facebook </button>  
                            </div><br></br>  
                            <div >
                            <button className="googlebutton"> Log in with Google </button>  
                            </div> <br></br>
                            </div> 
                    </div>
                </div>
            </div>
        )
    }
}
//export Login Component
export default Ownersignup;
