import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import eat from '../eat.png'
import taco from '../Tacobellnew.png';
// import '../css/letseat.css';
//Define a Login Component
class Letseat extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            result:"",
            restaurant_zipcode: "",
            dish_name: "",
            authFlag: false,
            failuser: false,
            delivery: false
        }
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount() {
        this.setState({
            authFlag: false
        })
    }
    //username change handler to update state variable with the text entered by the user

    // locationChangeHandler = (e) => {
    //     this.setState({
    //         Location: e.target.value
    //     })
    // }
    // //password change handler to update state variable with the text entered by the user
    // foodtypeChangeHandler = (e) => {
    //     this.setState({
    //         Foodtype: e.target.value
    //     })
    // }

    //submit Login handler to send a request to the node backend
    findFood = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            restaurant_zipcode: this.state.restaurant_zipcode,
            dish_name: this.state.dish_name
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/search', data)
            .then(response => {
                console.log("Status Code : ", response.status);
                if (response.status === 200) {
                    // <Redirect to="/home"> </Redirect>
                    this.setState({
                        authFlag: true,
                        result:response.data
                    })
                    console.log(this.state.result);
                } else {
                    this.setState({
                        authFlag: false

                    })
                }
            })
    }

    render() {
        //redirect based on successful login
        let redirectVar = null;
        if(!cookie.load('cookie')){
            redirectVar = <Redirect to= "/login"/>
        }

        if (this.state.authFlag){
            return (<Redirect to={{
              pathname: '/Searchitem',
              state: { result: this.state.result }
          }} />)
          }


        return (
            <div>
                {redirectVar}
                <div className="class-image" >
                    <img src={eat} style={{ width: "100%" }} />
                    <div className="image-text" >
                        <label className="deliverfood" > Who delivers in your neighbourhood ? </label>
                        <label className="delivery">Delivery </label><br></br > < br ></br>
                        < div class="row">
                            {/* <div class="col-md-4" >
                                {/* <input 
                                onChange ={(e) => { this.setState({type : e.target.value})  }}
                                    type="text"
                                    name="DeliveryType"
                                    placeholder="ASAP" /> */}
                            {/* </div>  */}

                            <div class="col-md-4" >
                                <input 
                                onChange ={(e) => { this.setState({restaurant_zipcode : e.target.value})  }}
                                    type="text"
                                    name="restaurant_zipcode"
                                    placeholder="Street address or zipcode" />
                            </div>

                            <div class="col-md-4" >
                                <input 
                                onChange ={(e) => { this.setState({dish_name : e.target.value})  }}
                                    type="text"
                                    name="dish_name"
                                    placeholder="pizza, sushi, c..." />
                            </div> <br></br >

                            <div class="col-md-4" >
                                <button type="button" onClick = {this.findFood} className="btn btn-primary" > Find Food </button> </div> <br></br >
                        </div>
                        <div > { /* <img src={} style={{ width: "100%" }} /> */}
                        </div>
                        <div>


                    </div>
                </div><br></br >

            </div> 


                {/* <button type="submit" onClick={this.submitLogin} className="button" > Sign in </button> <br></br > */}
            </div>


        )
    }
}
//export Login Component
export default Letseat;
