import React, { Component } from 'react'
import '../../App.css'
import './UpdateMenu.css'
import axios from 'axios'
import cookie from 'react-cookies'
import { Redirect } from 'react-router'
import { Link } from 'react-router-dom'
import { getProfile } from '../../actions'
import { loginuser } from '../../actions'
import { updaterestaurantmenu } from '../../actions'
import { menudetails } from '../../actions'

import { getUserImage } from '../../actions'
import { connect } from 'react-redux'
import DisplayOwnerNav from '../Display/DisplayOwnerNav'
import { Field, reduxForm } from 'redux-form'

// Define a Login Component
class Menu extends Component {
  // call the constructor method
  constructor (props) {
    super(props)
    this.state = {
      authFlag: false,
      menudtls: '',
      img : ''
    }
  }

  componentWillMount () {
    this.setState({
      authFlag: false
    })
    let temp = sessionStorage.getItem('menuid')
    // alert(temp)
    this.props.menudetails({ params: {id: temp} }, response => {
      // console.log( this.props.user[1])
      console.log(response)
      this.setState({
        menudtls: response.data
      })
    })


  }


  inputChangeHandler = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  onSubmit = formValues => {
    // let val = JSON.parse(sessionStorage.getItem('ownerdata'))
    let menuid = sessionStorage.getItem('menuid')
    // console.log('temp : ' +val.email)
    let data = {
      dishname: formValues.dishname,
      description: formValues.description,
      price: formValues.baseprice,
      section: formValues.section,
      id: menuid
    }
    axios.defaults.withCredentials = true

    this.props.updaterestaurantmenu(data, res => {
      if (res.status === 200) {
        console.log('Inside response', res.data[0])
        this.setState({
          authFlag: true
        })

        this.props.history.push('/ownerrestaurantmenu')
      } else {
        alert('Please enter valid credentials')
      }
    }) // ,(response)=>{ //   console.log('Redux test: ' + this.props.user); //   console.log('Redux response: ' + response); // }) // console.log(data); // this.props.login(data, (response)=>{ //   console.log('Redux test: ' + this.props.user) //   this.setState({ //     img: 'data:image/png;base64, ' + response.image //   }) // });

  }

  uploadImage = e => {
    e.preventDefault()
        const formData = new FormData()

        let email = sessionStorage.getItem('email')
        formData.append('myImage', this.state.file, email)

        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        axios
            .post('/updatemenu', formData, config)
            .then(response => {
                let data = { 'email': email }
                axios.post('http://localhost:3001/menuimage', data).then(response => {
                    console.log('Axios get:', response.data)
                    this.setState({
                        img: 'data:image/png;base64, ' + response.data
                    })
                })

            })
            .catch(error => { })

  }



  renderError = ({ error, touched }) => {
    if (touched && error) {
      return (
        <div>
          <label style={{ color: 'red' }}>{error}</label>
        </div>
      )
    }
  }

  renderInput = ({ input, label, text, meta}) => {
    return (
      <div>
        <div htmlFor='email' style={{ color: '#6b6b83' }}>
          {label}:  {text}
        </div>
        <input
          className='form-control'
          // style={{ marginRight: '10px' }}
          {...input}
        />
        {this.renderError(meta)}
      </div>
    )
  }

  render () {
    // redirect based on successful login
    // let redirectVar = null
    let invalidtag = null
    // if (!cookie.load('cookie')) {
    //   redirectVar = <Redirect to='/login' />
    // }



    let redirectVar  = null
    if (sessionStorage.getItem('JWT' === null)){ 
      redirectVar  = <Redirect to='/login'/>
    }


    if (this.state.authFailed) {
      invalidtag = (
        <label style={{ color: 'red' }}>*Invalid user name password!</label>
      )
    }
    let updatePic = null

    if (this.state.file !== '') {
      updatePic = (
        <button
          className='btn btn-link'
          type='submit'
        >
                    Update
        </button>
      )
    }

    return (
      <div>
        {redirectVar}
        <div className='row'>
          <div className='col-sm-2'>
            <DisplayOwnerNav />
          </div>

          <div className='col-sm-10'>
            <div class='container'>
              <div class='login-form'>
                {/* <div class='main-div'> */}

                <div className='row'>
                  <div className='col-sm-6'>

                  <form onSubmit={this.uploadImage} enctype='multipart/form-data'>
                <div>
                  <img
                    src={this.state.img}
                    width='200'
                    height='200'
                  />
                  <input
                    type='file'
                    onChange={this.imageChangeHandler}
                    name='myImage'
                    id='myImage'
                  />
                  <br />
                  {updatePic}
                </div>
              </form>

                    <form
                      className='ui form error'
                      onSubmit={this.props.handleSubmit(this.onSubmit)}
                    >
                      <ul class='list-group'>
                        <li class='list-group-item'>
                          <br />
                          <h2>Edit menu</h2>
                          <div
                            style={{ marginRight: '20px' }}
                            class='form-group'
                          >
                            <Field
                              class='form-control'
                              name='dishname'
                              component={this.renderInput}
                              label=' Name'
                              text= {this.state.menudtls.dish_name}
                            />
                          </div>
                          <div
                            style={{ marginRight: '20px' }}
                            class='form-group'
                          >
                            <Field
                              name='description'
                              type='text'
                              component={this.renderInput}
                              label='Description'
                              text= {this.state.menudtls.description}
                            />
                          </div>

                          <div
                            style={{ marginRight: '20px' }}
                            class='form-group'
                          >
                            <Field
                              name='section'
                              component={this.renderInput}
                              label='Menu Section'
                              text= {this.state.menudtls.section}
                            />
                          </div>
                          <br />
                          <div
                            style={{ marginRight: '20px' }}
                            class='form-group'
                          >
                            <Field
                              name='baseprice'
                              type='text'
                              component={this.renderInput}
                              label='Base Price'
                              text= {this.state.menudtls.price}
                            />
                          </div>
                          <br />
                          <button type='submit' class='btn btn-info'>
                            Edit Menu
                          </button>
                          <br />
                        </li>
                      </ul>
