import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';

//Define a Login Component
class Ownerlogin extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            Email: "",
            password: "",
            authFlag: false,
            failuser: false
        }
        //Bind the handlers to this class
        this.emailChangeHandler = this.emailChangeHandler.bind(this);
        this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
        this.submitLogin = this.submitLogin.bind(this);
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount() {
        this.setState({
            authFlag: false
        })
    }
    //username change handler to update state variable with the text entered by the user

    emailChangeHandler = (e) => {
        this.setState({
            Email: e.target.value
        })
    }
    //password change handler to update state variable with the text entered by the user
    passwordChangeHandler = (e) => {
        this.setState({
            password: e.target.value
        })
    }
    //submit Login handler to send a request to the node backend
    submitLogin = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            Email: this.state.Email,
            password: this.state.password
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/ownerlogin', data)
            .then(response => {
                console.log("Status Code : ", response.status);
                if (response.status === 200) {
                    // <Redirect to="/Letseat"> </Redirect>
                    this.setState({
                        authFlag: true
                    })
                }
                else {
                    this.setState({
                        authFlag: false

                    })
                }
            })
    }

    render() {
        //redirect based on successful login
        let redirectVar = null;
        if (this.state.authFlag) {
            redirectVar = <Redirect to="/lets-eat" />
        }

        return (
            <div>
                <nav class="navbar navbar-default">
                    {/* <img src= {require("../images/Grubhub.svg")} className ="homepage"></img> */}
                    <a class="navbar-brand" style={{ color: "red", fontFamily: "impact", fontWeight: "bold", fontSize: "25px" }} href="/">GRUBHUB</a>
                </nav>
                {redirectVar}
                <div class="container">

                    <div class="login-form">
                        <div class="main-div">
                            <div class="panel">
                                <h2 style={{ fontWeight: "bold", color: "black" }}><span style={{color:"red"}}>GRUBHUB</span> FOR RESTAURANTS</h2>


                            </div>
                            <div class="form-group">
                                <div>Username or email address</div>
                                <input onChange={this.emailChangeHandler} type="text" class="form-control" autoFocus name="Email" />
                            </div>
                            <div class="form-group">
                                <div>Password</div>
                                <input onChange={this.passwordChangeHandler} type="password" class="form-control" name="password" />
                            </div><br></br>


                            <button type="submit" className="btn btn-primary" onClick={this.submitLogin} >Sign in</button> <br></br><br></br>
                            <a href="#">Forgot Username</a><br></br>
                            <a href="#">Forgot Password</a>
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}
//export Login Component
export default Ownerlogin;
