import React, {Component} from 'react';
import axios from 'axios';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';


class Create extends Component{
    constructor(props){
        super(props);
        this.state = {
            BookID : "",
            Title : "",
            Author : "",
            redirect : false,
            failcreate:false,
            Bookiderror: ""
        }
    }  

validate =()=>{
    let BookID = this.state.BookID;
    let Bookiderror = null;
    let formisValid = true;
    if(BookID.length < 1){
        formisValid = false;
        Bookiderror = "Cannot be empty";
    }
    this.setState({Bookiderror : Bookiderror})
    return formisValid;
}

contactSubmit=(e)=>{
    e.preventDefault();

    if(this.validate()){
        alert("Form submitted");
    }
    else{
        alert("Form has errors.")
        }
    }
    
componentWillMount(){
    this.setState({
        redirect : false
    })
}
//username change handler to update state variable with the text entered by the user
BookIDChangeHandler = (e) => {
    
        this.setState({
            BookID : e.target.value
            })
    
}
//password change handler to update state variable with the text entered by the user
TitleChangeHandler = (e) => {
    this.setState({
        Title : e.target.value
    })
}
AuthorChangeHandler = (e) => {
    this.setState({
        Author : e.target.value
    })
}

//submit Login handler to send a request to the node backend
createBook = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
        BookID : this.state.BookID,
        Title : this.state.Title,
        Author : this.state.Author
        
    }
    //set the with credentials to true
    axios.defaults.withCredentials = true;
    //make a post request with the user data
    axios.post('http://localhost:3001/create',data)
        .then(response => {
            console.log("Status Code : ",response.status);
            if(response.status === 200){
                this.setState({redirect :true});
            }
        }).catch(err=>{
            this.setState({failcatch:true})
        })
}
    render(){
        let createlogin = null;
        
        if(!cookie.load('cookie')){
            createlogin = <Redirect to= "/login"/>
        }
        let redirectcreate = null;
        if (this.state.redirect) {
            redirectcreate = <Redirect to = {{ pathname: "/home" }} />;
            }

        let faillabelcatch = null;
        if(this.state.failcatch){
            faillabelcatch=(
            <label style = {{color:"red"}}>Book Already Exists</label>
            )
        }
    
        return(
            <div>
                {createlogin}
                {redirectcreate}
                <br/>
                <div class="container">
                    <form action="http://127.0.0.1:3000/create" method="post" onSubmit = {this.contactSubmit.bind(this)} noValidate>
                        <div style={{width: '30%'}} class="form-group">
                            <input onChange = {this.BookIDChangeHandler} type="number" class="form-control" name="BookID" placeholder="Book ID" noValidate/>
                            {this.state.Bookiderror}
                        </div>
                        <br/>
                        <div style={{width: '30%'}} class="form-group">
                                <input onChange = {this.TitleChangeHandler} type="text" class="form-control" name="Title" placeholder="Book Title" />
                        </div>
                        <br/>
                        <div style={{width: '30%'}} class="form-group">
                                <input onChange = {this.AuthorChangeHandler} type="text" class="form-control" name="Author" placeholder="Book Author" />
                        </div>
                        <br/>
                        <div style={{width: '30%'}}>
                    <button onClick = {this.createBook} class="btn btn-success" type="submit">Create</button>
                        </div> 
                    </form>
                    {faillabelcatch}
                </div>
            </div>
        )
    }
}

export default Create;
