import React, { Component } from 'react';
import '../App.css';
import '../css/Buyerprofile.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import '../css/new.css';

//Define a Login Component
class Buyerprofile extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            first_name: "",
            last_name: "",
            profilepic: '',
            email: "",
            password: "",
            nameflag: false,
            authFlag: false,
            failuser: false,
            selectedFile: null,
            phone: '',
            newEmail: ''
        }
    }

    componentWillMount() {
        let path = process.env.PUBLIC_URL
        path = path + '/profile/geolocation.png'

        this.setState({
            profilepic: path,
        })

        let temp = sessionStorage.getItem('email')

        let data = { email: temp }
        console.log('Inside will mount: data value is: ' + data.email)

        axios.get('http://localhost:3001/userprofile', {params:data},(data)=>{
        }).then((response) => {
                this.setState({
                    email: response.data.email,
                    phone: response.data.phone,
                    password: response.data.password,
                    first_name: response.data.first_name,
                    last_name: response.data.last_name
                })
                console.log(response.data);
            });

        axios.post('http://localhost:3001/userImage', data).then(response => {
            // update the state with the response data
            console.log('Axios get:', response.data)
            this.setState({
                profilepic: 'data:image/png;base64, ' + response.data
            })
        })
    }


    // uploadHandler = e => {
    //     const fd = new FormData();
    //     fd.append('selectedFile', this.state.selectedFile);
    //     axios.post('http://localhost:3001/userprofile', fd)
    //         .then(response => {
    //             axios.post('http://localhost:3001/getprofilepic', fd)
    //                 .then(response => {
    //                     console.log(response);
    //                     let newpic = 'data:image/jpg;base64, ' + response.data;
    //                     this.setState({
    //                         profilepic: newpic
    //                     })
    //                 })
    //         })
    // }

    //username change handler to update state variable with the text entered by the user
    inputChangeHandler = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    imageChangeHandler = e => {
        // console.log('image change handle name: ' + e.target.name)
        // console.log('image change handle value: ' + e.target.files[0])
        this.setState({
            file: e.target.files[0]
        })
    }


    update = (e) => {
        const data = {
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            email: this.state.email,
            phone: this.state.phone,
            password: this.state.password
        }
        console.log(data);
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/update', data)
            .then(response => {
                console.log("Status Code  is : ", response.status);
                console.log(response.data[0]);
                if (response.status === 200) {
                    this.setState({
                        first_name: response.data[0].first_name,
                        last_name: response.data[0].last_name,
                        password: response.data[0].password,
                        email: response.data[0].email,
                        phone: response.data[0].phone

                    });
                    alert("Profile Update Succefully");
                } else {
                    console.log('Change failed !!! ');

                }
            });
    }

    uploadImage = e => {
        e.preventDefault()
        // var headers = new Headers();
        const formData = new FormData()
        // console.log(this.state.file.name)

        let email = sessionStorage.getItem('email')
        // this.setState({file.name:email})
        // var imagedata = document.querySelector('input[type="file"]').files[0];
        formData.append('myImage', this.state.file, email)
        // formData.append('myImage', this.state.file, email )
        // formData.append('email', email)
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        axios
            .post('/userprofile', formData, config)
            .then(response => {
                let data = { 'email': email }
                axios.post('http://localhost:3001/userimage', data).then(response => {
                    // update the state with the response data
                    console.log('Axios get:', response.data)
                    this.setState({
                        profilepic: 'data:image/png;base64, ' + response.data
                    })
                })
                console.log('The file is successfully uploaded')
            })
            .catch(error => { })
        // prevent page from refresh
    }



    render() {
        let updatePic = null

        if (this.state.file !== '') {
            updatePic = (
                <button
                    className='btn btn-link'
                    type='submit'
                >
                    Update
        </button>
            )
        }
        //redirect based on successful login
        let createlogin = null;

        if (!cookie.load('cookie')) {
            createlogin = <Redirect to="/login" />
        }
        let redirectVar = null;
        if (this.state.authFlag) {
            redirectVar = <Redirect to="/login" />
        }

        return (
            <div>
                {createlogin}
                {redirectVar}
                <div className="row">
                    <div className="col-sm-2">
                        <div className="col-shift">
                            <h2>Your account</h2>
                            <ul class="nav flex-column">
                                <li className="new-change">
                                    <a className="nav-link active" href="/userprofile" >
                                        <span className="profilenew">Profile</span></a></li>
                                <li><a className="nav-link" href="#" >
