import React, { Component } from 'react'
import '../../App.css'
// import './Orders.css'
import axios from 'axios'
import cookie from 'react-cookies'
import { Redirect } from 'react-router'
import { Link } from 'react-router-dom'
import { getUserOrder } from '../../actions/index'
import { connect } from 'react-redux'
import BuyerOwnerNav from '../Display/BuyerOwnerNav'
import { Field, reduxForm } from 'redux-form';
// import { Card, CardBody, Button, CardTitle, CardText, CardImg } from 'reactstrap';
import { Card, Button, CardTitle, CardText } from 'reactstrap';
// Define a Login Component
class Orders extends Component {
  // call the constructor method
  constructor (props) {
    // Call the constrictor of Super class i.e The Component
    super(props)
    // maintain the state required for this component
    this.state = {
      records: ''
    }
  }

  componentWillMount () {
    // let temp =
    let data = {
      email: sessionStorage.getItem('email'),
      status: 'new',
      person: 'user_email'
    }
    axios.defaults.withCredentials = true

    this.props.getUserOrder({ params: data }, response => {
      // console.log( this.props.user[1])
      console.log(response)
      this.setState(
        {
          records: response.data
        },
        () => {
          // sessionStorage.setItem('records', JSON.stringify(this.state.records))
          // console.log(this.state.restaurantdetails)
          Object.keys(this.state.records).map(row =>
            console.log(this.state.records[row].rating)
          )
        }
      )
      // sessionStorage.setItem('sections', this.state.sections)

      // alert(sessionStorage.getItem('sections'))
    })
  }

  inputChangeHandler = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render () {
    // redirect based on successful login
    let redirectVar = null
    let invalidtag = null
    // let itemslist = null
    let items = null
    let total = 0

    if (!cookie.load('cookie')) {
      redirectVar = <Redirect to='/login' />
    }

    let itemslist = (items, status) => {
      return Object.keys(items).map(item => {
        console.log('List', item)
        total = total + items[item][1] * items[item][0]
        return (
          <div>
            <div className='row'>
              <div className='col-sm-3'>
                <div>{item}</div>
              </div>
              <div className='col-sm-3'>
                <div>{items[item][0]}</div>
              </div>
              <div className='col-sm-3'>
                <div>{items[item][1] * items[item][0]}</div>
              </div>
            </div>
          </div>
        )
      })
    }

    let orderRecords = null
    // let list = JSON.parse(sessionStorage.getItem('records'))
    let list = this.state.records
    // console.log(list);
    let i=0;
    for(i=0;i<list.length;i+2){
      return (
              <div>
               <li  style ={{backgroundColor : "red"}}class='list-group-item' >
                <div>
                  <div>
                    <div className='row' draggable>
                    <div className='col-sm-9'>
                    <label
                        style={{
                          marginLeft: '10px',

                          color: 'black'
                        }}
                      >
                        {list[i].restaurant_name}
                      </label>
                      </div>
                      <label style={{
                          color: 'black'
                        }} className='col-sm-3'>
                      {list[i].order_status}
                      </label>

                    </div>
                    <br />
                    <div style={{ marginLeft: '10px' }} className='row'>
                      {itemslist(
                        JSON.parse(list[i].orderlist),
                        list[i].order_status
                      )}
                    </div>
                    <br />
                    <div className='row' draggable>
                      <div className='col-sm-6'>
                        <label style={{
                          color: 'black'}}>Total</label>
                      </div>
                      <div className='col-sm-3'>
                        <label style={{
                          color: 'black'}}>$ {list[i].total}</label>
                      </div>
                    </div>

                    <br />
                    <br></br>

                    {/* <li  style ={{backgroundColor : "red"}}class='list-group-item' ></li> */}
                    <div className='row'>
                    <div className='col-sm-9'>
                    <label
                        style={{
                          marginLeft: '10px',

                          color: 'black'
                        }}
                      >
                        {list[i+1].restaurant_name}
                      </label>
                      </div>
                      <label style={{
                          color: 'black'
                        }} className='col-sm-3'>
                      {list[i+1].order_status}
                      </label>

                    </div>
                    <br />
                    <div style={{ marginLeft: '10px' }} className='row'>
                      {itemslist(
                        JSON.parse(list[i+1].orderlist),
                        list[i+1].order_status
                      )}
                    </div>
                    <br />
                    <div className='row'>
                      <div className='col-sm-6'>
                        <label style={{
                          color: 'black'}}>Total</label>
                      </div>
                      <div className='col-sm-3'>
                        <label style={{
                          color: 'black'}}>$ {list[i+1].total}</label>
                      </div>
                    </div>
                  </div>
                </div>
              </li> 
              </div>
            )
    }
    if (list !== null) {
      orderRecords = Object.keys(list).map(row => {
        // console.log(list[row])

        // Object.keys(list[row]).map(item => {
        //   console.log(list[row][item])
        // console.log('List',list)
        //   total = total + items[item][1] * items[item][0]
        //   // total = total.toFixed();
        return (

          <div>
           <li style = {{backgroundColor : "red"}} class='list-group-item' draggable>
            <div>
              <div>
                <div className='row'>
                <div className='col-sm-9'>
                <label
                    style={{
                      marginLeft: '10px',

                      color: 'black'
                    }}
                  >
                    {list[row].restaurant_name}
                  </label>
                  </div>
                  <label style={{
                      color: 'black'
                    }} className='col-sm-3'>
                  {list[row].order_status}
                  </label>

                </div>
                <br />
                <div style={{ marginLeft: '10px' }} className='row'>
                  {itemslist(
                    JSON.parse(list[row].orderlist),
                    list[row].order_status
                  )}
                </div>
                <br />
                <div className='row'>
                  <div className='col-sm-6'>
                    <label style={{
                      color: 'black'}}>Total</label>
                  </div>
                  <div className='col-sm-3'>
                    <label style={{
                      color: 'black'}}>$ {list[row].total}</label>
                  </div>
                </div>
                <br />
              </div>
            </div>
          </li> 
          </div>
        )
      })
      // })
    }

    // let orderRecords =

    return (
      <div>
        {redirectVar}
        <div className='row'>
