import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';

//Define a Login Component
class Ownersignup extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            Name: "",
            Email: "",
            password: "",
            RestaurantName: "",
            RestaurantZipcode: "",
            authFlag: false,
            failuser: false
        }
        //Bind the handlers to this class
        this.nameChangeHandler = this.nameChangeHandler.bind(this);
        this.emailChangeHandler = this.emailChangeHandler.bind(this);
        this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
        this.restaurantNameHandler = this.restaurantNameChangeHandler.bind(this);
        this.restaurantZipcodeHandler = this.restaurantZipcodeChangeHandler.bind(this);
        this.submitOwnerSignup = this.submitOwnerSignup.bind(this);
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount() {
        this.setState({
            authFlag: false
        })
    }
    //username change handler to update state variable with the text entered by the user
    nameChangeHandler = (e) => {
        this.setState({
            Name: e.target.value
        })
    }



    emailChangeHandler = (e) => {
        this.setState({
            Email: e.target.value
        })
    }
    //password change handler to update state variable with the text entered by the user
    passwordChangeHandler = (e) => {
        this.setState({
            password: e.target.value
        })
    }

    restaurantNameChangeHandler = (e) => {
        this.setState({
            Name: e.target.value
        })
    }

    restaurantZipcodeChangeHandler = (e) => {
        this.setState({
            Name: e.target.value
        })
    }
    //submit Login handler to send a request to the node backend
    submitOwnerSignup = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            Name: this.state.Name,
            Email: this.state.Email,
            password: this.state.password,
            RestaurantName: this.state.RestaurantName,
            RestaurantZipcode: this.state.RestaurantZipcode
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/ownersignup', data)
            .then(response => {
                console.log("Status Code : ", response.status);
                if (response.status === 200) {
                    // <Redirect to="/home"> </Redirect>
                    this.setState({
                        authFlag: true
                    })
                }
                else {
                    this.setState({
                        authFlag: false

                    })
                }
            })
    }

    render() {
        //redirect based on successful login
        let redirectVar = null;
        if (this.state.authFlag) {
            redirectVar = <Redirect to="/login" />
        }

        return (
            <div>
                <nav class="navbar navbar-default">
                    {/* <img src= {require("../images/Grubhub.svg")} className ="homepage"></img> */}
                    <a class="navbar-brand" style={{ color: "red", fontFamily: "impact", fontWeight: "bold", fontSize: "25px" }} href="/">GRUBHUB</a>
                </nav>
                {redirectVar}
                <div class="container">

                    <div class="login-form">
                        <div class="main-div">
                            <div class="panel">
                                <h2 style={{ fontWeight: "bold", color: "black" }}>Create your account</h2>
                            </div>

                            <div>
                                <div class="row">
                                    <div class="col-sm-6">
                                        <label style={{ fontWeight: "normal" }}>First name</label>
                                    </div>
                                    <div class="col-sm-6">
                                        <label style={{ fontWeight: "normal" }}>Last name</label>
                                    </div>
                                </div>
                            </div>

                            <div class="row">
                                <div class="col-sm-6">
                                    <input required onChange={this.firstnameChangeHandler} type="text" class="form-control" name="firstname" />
                                </div>
                                <div class="col-sm-6">
                                    <input onChange={this.lastnameChangeHandler} type="text" class="form-control" name="lastname" />
                                </div>
                            </div>


                            <div>
                                <label style={{ fontWeight: "normal" }}>Email</label>
                            </div>

                            <div class="form-group">
                                <input onChange={this.emailChangeHandler} type="text" class="form-control" name="Email" />
                            </div>

                            <div>
                                <label style={{ fontWeight: "normal" }}>Password</label>
                            </div>

                            <div class="form-group">
                                <input onChange={this.passwordChangeHandler} type="password" class="form-control" name="password" />
                            </div>

                            <div>
                                <label style={{ fontWeight: "normal" }}>Restaurant Name</label>
                            </div>

                            <div class="form-group">
                                <input onChange={this.restaurantNameChangeHandler} type="text" class="form-control" name="RestaurantName" placeholder="Restaurant Name" />
                            </div>

                            <div>
                                <label style={{ fontWeight: "normal" }}>Zipcode</label>
                            </div>

                            <div class="form-group">
                                <input onChange={this.restaurantZipcodeHandler} type="text" class="form-control" name="RestaurantZipcode" placeholder="Zip code" />
                            </div>

                            <button type="submit" onClick={this.submitSignup} class="btn btn-primary">Create your account</button> <br></br><br></br>
                            <div style={{ textAlign: "center" }}>or</div>

                            <div className="heading1">

                            </div><br></br>

                            <div>
                                <button type="button" style={{ backgroundColor: "#3b5998", color: "white" }} className="btn btn-primary"> Log in with Facebook </button>
                            </div><br></br>

                            <div>
                                <button type="button" style={{ backgroundColor: "#4285f4", color: "white" }} className="btn btn-primary"> Log in with Google </button>
                            </div> <br></br>

                            <div className="textAligncenter">
                                <label >Have an account?<span style={{ color: "blue" }}><a href="/login">Sign in</a></span></label>
                                {/* <div><Link to="/login">Sign in</Link></div> */}
                            </div><br></br>
                            <p style={{ fontSize: "10px" }}>By creating your Grubhub account, you agree to the <span style={{ color: "blue" }}>Terms of Use </span>and <span style={{ color: "blue" }}>Privacy Policy</span>  </p>


                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
//export Login Component
export default Ownersignup;
