import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import { Redirect } from 'react-router';
import { Link } from 'react-router-dom';
import eat from '../eat.png'
import taco from '../Tacobellnew.png';
// import '../css/letseat.css';
//Define a Login Component
class Letseat extends Component {
    //call the constructor method
    constructor(props) {
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            Location: "",
            Foodtype: "",
            authFlag: false,
            failuser: false,
            delivery: false
        }
        //Bind the handlers to this class
        this.locationChangeHandler = this.locationChangeHandler.bind(this);
        this.foodtypeChangeHandler = this.foodtypeChangeHandler.bind(this);
        this.findFood = this.findFood.bind(this);
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount() {
        this.setState({
            authFlag: false
        })
    }
    //username change handler to update state variable with the text entered by the user

    locationChangeHandler = (e) => {
        this.setState({
            Location: e.target.value
        })
    }
    //password change handler to update state variable with the text entered by the user
    foodtypeChangeHandler = (e) => {
        this.setState({
            Foodtype: e.target.value
        })
    }

    //submit Login handler to send a request to the node backend
    findFood = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            Location: this.state.Location,
            Foodtype: this.state.Foodtype
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/lets-eat', data)
            .then(response => {
                console.log("Status Code : ", response.status);
                if (response.status === 200) {
                    // <Redirect to="/home"> </Redirect>
                    this.setState({
                        authFlag: true
                    })
                } else {
                    this.setState({
                        authFlag: false

                    })
                }
            })
    }

    render() {
        //redirect based on successful login
        let redirectVar = null;
        if (cookie.load('cookie')) {
            redirectVar = <Redirect to="/lets-eat"/>
        }

        return (
            <div>
                <nav class="navbar navbar-default">
                    <ul class="nav navbar-nav navbar-right">
                        <li> < Link to="/userprofile" onClick={this.handleLogout} >< span class="glyphicon glyphicon-user"> </span>Account</Link > </li>
                    </ul>
                    <a class="navbar-brand" href="/" style={{color:"red",fontFamily:'impact',fontSize:"25px",fontWeight:'bold'}}> GRUBHUB</a>
                </nav>
                <div className="class-image" >
                    <img src={eat} style={{ width: "100%" }} />
                    <div className="image-text" >
                        <label className="deliverfood" > Who delivers in your neighbourhood ? </label>
                        <label className="delivery">Delivery </label><br></br > < br ></br>
                        < div class="row">
                            <div class="col-md-4" >
                                <input onChange={this.ChangeHandler}
                                    type="text"
                                    name="DeliveryType"
                                    placeholder="ASAP" />
                            </div>

                            <div class="col-md-4" >
                                <input onChange={this.locationChangeHandler}
                                    type="text"
                                    name="Location"
                                    placeholder="Street address or zipcode" />
                            </div>

                            <div class="col-md-4" >
                                <input onChange={this.foodtypeChangeHandler}
                                    type="text"
                                    name="FoodType"
                                    placeholder="pizza, sushi, c..." />
                            </div> <br></br >

                            <div class="col-md-4" >
                                <button type="button" className="btn btn-primary" > Find Food </button> </div> <br></br >
                        </div>
                        <div > { /* <img src={} style={{ width: "100%" }} /> */}
                        </div>


                    </div>
                </div><br></br >

                <div>

                </div>
                <button type="submit" onClick={this.submitLogin} className="button" > Sign in </button> <br></br >
            </div>


        )
    }
}
//export Login Component
export default Letseat;
