import React, {Component} from 'react';
import '../App.css';
import axios from 'axios';
import cookie from 'react-cookies';
import {Redirect} from 'react-router';
import {Link} from 'react-router-dom';

//Define a Login Component
class Buyersignup extends Component{
    //call the constructor method
    constructor(props){
        //Call the constrictor of Super class i.e The Component
        super(props);
        //maintain the state required for this component
        this.state = {
            firstname : "",
            lastname : "",
            Email : "",
            password : "",
            authFlag : false,
            failuser: false
        }
        //Bind the handlers to this class
        this.firstnameChangeHandler = this.firstnameChangeHandler.bind(this);
        this.lastnameChangeHandler = this.lastnameChangeHandler.bind(this);
        this.emailChangeHandler = this.emailChangeHandler.bind(this);
        this.passwordChangeHandler = this.passwordChangeHandler.bind(this);
        this.submitSignup = this.submitSignup.bind(this);
    }
    //Call the Will Mount to set the auth Flag to false
    componentWillMount(){
        this.setState({
            authFlag : false
        })
    }
    //username change handler to update state variable with the text entered by the user
    firstnameChangeHandler = (e) => {
        this.setState({
            firstname : e.target.value
        })
    }

    lastnameChangeHandler = (e) => {
        this.setState({
            lastname : e.target.value
        })
    }

    emailChangeHandler = (e) => {
        this.setState({
            Email : e.target.value
        })
    }
    //password change handler to update state variable with the text entered by the user
    passwordChangeHandler = (e) => {
        this.setState({
            password : e.target.value
        })
    }
    //submit Login handler to send a request to the node backend
    submitSignup = (e) => {
        var headers = new Headers();
        //prevent page from refresh
        e.preventDefault();
        const data = {
            firstname : this.state.firstname,
            lastname : this.state.lastname,
            Email : this.state.Email,
            password : this.state.password
        }
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        //make a post request with the user data
        axios.post('http://localhost:3001/signup',data)
            .then(response => {
                console.log("Status Code : ",response.status);
                if(response.status === 200){
                    // <Redirect to="/home"> </Redirect>
                    this.setState({
                        authFlag : true
                    })
                }
                else{
                    this.setState({
                    authFlag : false

                    })
                }
            })
    }

    render(){
        //redirect based on successful login
        let redirectVar = null;
        if(this.state.authFlag){
            redirectVar = <Redirect to= "/login"/>
        }

        return(
            <div>
                {redirectVar}
            <div class="container">

                <div class="login-form">
                    <div class="main-div">
                        <div class="panel">
                            <h2 style={{fontWeight:"bold",color:"black"}}>Create your account</h2>
                        </div>  

                        <div>

                        <div class="row">
                        <div class="col-sm-6">
                        <label style={{fontWeight:"normal"}}>First name</label>
                        </div>
                        <div class="col-sm-6">   
                        <label style={{fontWeight:"normal"}}>Last name</label>
                        </div>
                        </div>
                        </div>

                            <div>
                            <div class="col-sm-6">
                            <div class="form-group">
                                <input required onChange = {this.firstnameChangeHandler} type="text" class="form-control" name="firstname" />
                                </div>
                        </div>
                        <div class="col-sm-6">   
                        <div class="form-group">
                                <input onChange = {this.lastnameChangeHandler} type="text" class="form-control" name="lastname"/>
                            </div>
                        </div>
                        </div>


                            <div>
                                    <label style={{fontWeight:"normal"}}>Email</label>
                                </div>

                            <div class="form-group">
                                <input onChange = {this.emailChangeHandler} type="text" class="form-control" name="Email" />
                            </div>

                            <div>
                                    <label style={{fontWeight:"normal"}}>Password</label>
                                </div>

                            <div class="form-group">
                                <input onChange = {this.passwordChangeHandler} type="password" class="form-control" name="password" />
                            </div><br></br>

                            <button type="submit" onClick = {this.submitSignup} class="btn btn-primary">Create your account</button> <br></br><br></br>
                            <div style={{textAlign:"center"}}>or</div>

                            <div className="heading1">

                            </div><br></br>

                            <div>
                            <button type ="button" style={{backgroundColor:"#3b5998",color:"white"}}className="btn btn-primary"> Log in with Facebook </button>  
                            </div><br></br> 

                            <div>
                            <button type ="button" style={{backgroundColor:"#4285f4",color:"white"}} className="btn btn-primary"> Log in with Google </button>  
                            </div> <br></br>

                            <div>
                                <label style={{fontWeight:"normal"}}>Have an account?</label>
                                <div style={{textAlign:"center"}}><Link to="/signup">Sign in</Link></div>
                            </div>


                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
//export Login Component
export default Buyersignup;
