import React, {Component} from 'react';

class Delete extends Component{
    constructor(){
        super();
        this.state = {
            BookID : "",
            Title : "",
            Author : "",
            redirectnew : false
        }
    }  

componentWillMount(){
    this.setState({
        redirect : false
    })
}
//username change handler to update state variable with the text entered by the user
BookIDChangeHandler = (e) => {
    this.setState({
        BookID : e.target.value
    })
}

//submit Login handler to send a request to the node backend
createBook = (e) => {
    var headers = new Headers();
    //prevent page from refresh
    e.preventDefault();
    const data = {
        BookID : this.state.BookID,
        Title : this.state.Title,
        Author : this.state.Author
    }
    //set the with credentials to true
    axios.defaults.withCredentials = true;
    //make a post request with the user data
    axios.post('http://localhost:3001/create',data)
        .then(response => {
            console.log("Status Code : ",response.status);
            if(response.status === 200){
                this.setState({redirect :true});
            }
        
        });
}
    render(){
        return(
            <div class="container">
                <form>
                    <div style={{width: "50%",float: "left"}} class="form-group">
                        <input onChange = {this.BookIDChangeHandler} type="text" class="form-control" name="BookID" placeholder="Search a Book by Book ID"/>
                    </div>
                    <div style={{width: "50%", float: "right"}}>
                            <button onclick = {this.deleteBook} class="btn btn-success" type="submit">Delete</button>
                    </div> 
                </form>
            </div>
        )
    }
}

export default Delete;
